import React, { useState, useEffect } from 'react';
import { UserSession } from "@stacks/connect";
import AssetCard from '../AssetCard';
import TestNetNFTSList from '../TestNetNFTSList';
export const userSession = new UserSession();

function Profile() {
  // Initialise the state for the profile and STX balances
  const [profile, setProfile] = useState({
    name: "John Smith",
    profileImage: null, // Initially no profile image
  });

  const [mainnetBalance, setMainnetBalance] = useState(null);
  const [testnetBalance, setTestnetBalance] = useState(null);

  // Retrieve the STX addresses from the user session
  const mainnetAddress = userSession.loadUserData().profile.stxAddress.mainnet;
  const testnetAddress = userSession.loadUserData().profile.stxAddress.testnet;

  // Function to handle file input change (uploading profile image)
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        // Set profile image to the uploaded file
        setProfile({ ...profile, profileImage: event.target.result });
      };
      reader.readAsDataURL(file);
    }
  };

  const triggerFileInput = () => {
    document.getElementById("profileImageInput").click();
  };

  // Fetch the STX balance
  useEffect(() => {
    async function fetchBalance(address, network) {
      try {
        const response = await fetch(
          `https://stacks-node-api.${network}.stacks.co/extended/v1/address/${address}/balances`
        );
        const data = await response.json();
        return data.stx.balance;
      } catch (error) {
        console.error('Error fetching STX balance:', error);
        return null;
      }
    }

    // Fetch both mainnet and testnet balances
    fetchBalance(mainnetAddress, "mainnet").then((balance) => {
      if (balance) {
        setMainnetBalance(balance / 1000000); // Convert micro-STX to STX
      }
    });

    fetchBalance(testnetAddress, "testnet").then((balance) => {
      if (balance) {
        setTestnetBalance(balance / 1000000); // Convert micro-STX to STX
      }
    });
  }, [mainnetAddress, testnetAddress]);

  return (
    <main>
      <section className="profile-container">
        <div className="profile-info">
          <img
            src={profile.profileImage || "https://via.placeholder.com/150"}
            alt="Profile"
            className="profile-pic"
            onClick={triggerFileInput}
            style={{ cursor: 'pointer' }}
          />
          <h1>{profile.name}</h1>
          <p>Mainnet Wallet Address: {mainnetAddress}</p>
          <p>Testnet Wallet Address: {testnetAddress}</p>

          {/* Displaying balances */}
          <p>Mainnet STX Balance: {mainnetBalance !== null ? `${mainnetBalance} STX` : 'Loading...'}</p>
          <p>Testnet STX Balance: {testnetBalance !== null ? `${testnetBalance} STX` : 'Loading...'}</p>

          <input 
            type="file" 
            accept="image/*" 
            id="profileImageInput"
            style={{ display: 'none' }}
            onChange={handleImageUpload}
          />
        </div>

        <div className="profile-actions">
          <button>My Transactions</button>
          <button>Edit Profile</button>
        </div>

        <div style={{ marginBottom: '20px' }}>
          <TestNetNFTSList />
        </div>

        <div style={{ marginTop: '20px' }}>
          <AssetCard />
        </div>
        
      </section>
    </main>
  );
}

export default Profile;
