import React, { useState, useEffect } from 'react';
import { callReadOnlyFunction } from '@stacks/transactions'; // Import the necessary library functions
import { StacksTestnet } from "@stacks/network";
import { 
    AnchorMode, 
    principalCV, 
    uintCV, 
    PostConditionMode 
  } from "@stacks/transactions";

const CurrentListings = ({ contractAddress, contractName }) => {
  const [listings, setListings] = useState([]);
  const [loading, setLoading] = useState(true);

  // Function to get the last listing ID
  const fetchLastListingId = async () => {
    const options = {
    network: new StacksTestnet(),
      contractAddress: "ST1NWPSRC02Z9A20RHSBGDEDG9H8CHS6ENJ2N3TTH",
      contractName: "tiny-market",
      functionName: 'last-listing-id',
      functionArgs: [],
      senderAddress: yourAddress,
    };
    
    const result = await callReadOnlyFunction(options);
    return Number(result.value); // Get the number from the result
  };

  // Function to fetch individual listing by ID
  const fetchListing = async (listingId) => {
    const options = {
      contractAddress,
      contractName,
      functionName: 'get-listing',
      functionArgs: [listingId],
      network: yourStacksNetwork,
      senderAddress: yourAddress,
    };
    
    const result = await callReadOnlyFunction(options);
    return result.value ? result.value : null;
  };

  // Fetch all active listings
  const fetchAllListings = async () => {
    setLoading(true);
    try {
      const lastListingId = await fetchLastListingId();
      const fetchedListings = [];
      
      for (let id = 0; id < lastListingId; id++) {
        const listing = await fetchListing(id);
        if (listing && listing.expiry > currentBlockHeight) {
          fetchedListings.push(listing);
        }
      }
      setListings(fetchedListings);
    } catch (error) {
      console.error('Error fetching listings:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAllListings(); // Fetch listings on component mount
  }, []);

  if (loading) {
    return <div>Loading listings...</div>;
  }

  return (
    <div>
      <h2>Current NFT Listings</h2>
      {listings.length === 0 ? (
        <p>No listings available</p>
      ) : (
        <ul>
          {listings.map((listing, index) => (
            <li key={index}>
              Token ID: {listing.tokenId} - Price: {listing.price} STX
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default CurrentListings;
