import React, { useState } from 'react';
import { useConnect } from "@stacks/connect-react";
import { StacksMocknet } from "@stacks/network";
import { AnchorMode, 
    principalCV, 
    uintCV, 
    makeContractCall, 
    boolCV } 
    from "@stacks/transactions";
import { userSession } from "./ConnectWallet";

const ListingForm = () => {
  const { doContractCall } = useConnect();

  const [nftAssetContract, setNftAssetContract] = useState('');
  const [tokenId, setTokenId] = useState('');
  const [expiry, setExpiry] = useState('');
  const [price, setPrice] = useState('');
  const [paymentAssetContract, setPaymentAssetContract] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    const paymentAsset = paymentAssetContract ? principalCV(paymentAssetContract) : null;

    doContractCall({
      network: new StacksMocknet(),
      anchorMode: AnchorMode.Any,
      contractAddress: "ST1PQHQKV0RJXZFY1DGX8MNSNYVE3VGZJSRTPGZGM", // Address of the contract containing `list-asset`
      contractName: "tiny-market", // Contract name
      functionName: "list-asset",
      functionArgs: [
        principalCV(nftAssetContract), // Convert NFT contract address to Clarity principal
        {
          taker: null,
          'token-id': uintCV(parseInt(tokenId)),
          expiry: uintCV(parseInt(expiry)),
          price: uintCV(parseInt(price)),
          paymentAssetContract: paymentAsset ? paymentAsset : null,
        }
      ],
      onFinish: (data) => {
        window.alert("Asset listed successfully!");
      },
      onCancel: () => {
        console.log("onCancel:", "Transaction was canceled");
        window.alert("Asset listing failed.");
      },
    });
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="nft-asset-contract">NFT Asset Contract Principal:</label>
        <input
          type="text"
          id="nft-asset-contract"
          value={nftAssetContract}
          onChange={(e) => setNftAssetContract(e.target.value)}
          required
        />
      </div>

      <div>
        <label htmlFor="token-id">NFT Token ID:</label>
        <input
          type="number"
          id="token-id"
          value={tokenId}
          onChange={(e) => setTokenId(e.target.value)}
          required
        />
      </div>

      <div>
        <label htmlFor="expiry">Expiry Block Height:</label>
        <input
          type="number"
          id="expiry"
          value={expiry}
          onChange={(e) => setExpiry(e.target.value)}
          required
        />
      </div>

      <div>
        <label htmlFor="price">Price:</label>
        <input
          type="number"
          id="price"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
          required
        />
      </div>

      <div>
        <label htmlFor="payment-asset-contract">Payment Asset Contract (optional):</label>
        <input
          type="text"
          id="payment-asset-contract"
          value={paymentAssetContract}
          onChange={(e) => setPaymentAssetContract(e.target.value)}
        />
      </div>

      <button type="submit">List Asset</button>
    </form>
  );
};

export default ListingForm;
