import React, { useState } from 'react';
import AssestCard from '../AssetCard';
import Mint from '../Mint';
import MintedNFTs from '../NFTlist';

function Home() {
  // State to trigger a refresh of the NFT list
  const [refresh, setRefresh] = useState(false);

  // Define the onMint function to trigger a refresh
  const handleMint = () => {
    console.log("NFT minted successfully");
    setRefresh(prev => !prev); // Toggle the refresh state
  };

  return (
    <div>
      <div>
        <AssestCard />
      </div>
    
      <div>
        {/* Pass the onMint function as a prop to Mint */}
        <Mint onMint={handleMint} />
      </div>

      <div>
        {/* Pass the refresh state to MintedNFTs to trigger re-fetch */}
        <MintedNFTs refresh={refresh} />
      </div>
    </div>
  );
}

export default Home;
