import React, { useState } from 'react';
import { useConnect } from "@stacks/connect-react";
import { StacksMocknet } from "@stacks/network";
import { doContractCall, AnchorMode, principalCV, uintCV } from '@stacks/transactions';
import { userSession } from "./ConnectWallet";

const ListingForm = () => {
  const { doContractCall } = useConnect();

  const [nftAssetContract, setNftAssetContract] = useState('');
  const [tokenId, setTokenId] = useState('');
  const [expiry, setExpiry] = useState('');
  const [price, setPrice] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
  
    const nftAssetContract = document.getElementById('nft-asset-contract').value;
    const tokenId = document.getElementById('token-id').value;
    const expiry = document.getElementById('expiry').value;
    const price = document.getElementById('price').value;
  
    console.log('nftAssetContract:', nftAssetContract);
    console.log('tokenId:', tokenId);
    console.log('expiry:', expiry);
    console.log('price:', price);
  
    if (!nftAssetContract || !tokenId || !expiry || !price) {
      window.alert("Please fill in all fields.");
      return;
    }
  
    try {
      doContractCall({
        network: new StacksMocknet(),
        anchorMode: AnchorMode.Any,
        contractAddress: "ST1PQHQKV0RJXZFY1DGX8MNSNYVE3VGZJSRTPGZGM",
        contractName: "tiny-market",
        functionName: "list-asset",
        functionArgs: [
          principalCV(nftAssetContract),
          uintCV(parseInt(tokenId)),
          uintCV(parseInt(expiry)),
          uintCV(parseInt(price)),
        ],
        onFinish: (data) => {
          window.alert("Asset listed successfully!");
        },
        onCancel: () => {
          console.log("onCancel:", "Transaction was canceled");
          window.alert("Asset listing failed.");
        },
        onError: (error) => {
          console.error("Error during contract call:", error);
          window.alert("An error occurred while listing the asset. Please try again.");
        }
      });
    } catch (error) {
      console.error("Error initiating contract call:", error);
      window.alert("An unexpected error occurred. Please try again.");
    }
  };
  
  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="nft-asset-contract">NFT Asset Contract Principal:</label>
        <input type="text" id="nft-asset-contract" />
      </div>
      <div>
        <label htmlFor="token-id">Token ID:</label>
        <input type="text" id="token-id" />
      </div>
      <div>
        <label htmlFor="expiry">Expiry:</label>
        <input type="text" id="expiry" />
      </div>
      <div>
        <label htmlFor="price">Price:</label>
        <input type="text" id="price" />
      </div>
      <button type="submit">Submit</button>
    </form>
  );
}

  export default ListingForm;
