import React, { useState } from 'react';
import AssestCard from '../AssetCard';
import Mint from '../Mint';
import MintedNFTs from '../NFTlist';

function Home() {
  const [minted, setMinted] = useState(false);

  // Callback function to trigger when minting is done
  const handleMint = () => {
    console.log("NFT minted callback triggered");
    setMinted(!minted); // Toggle the state to trigger a refresh
  };

  return (
    <div>
      <div>
        <AssestCard />
      </div>
    
      <div>
        <Mint onMint={handleMint} /> {/* Pass the callback to Mint component */}
      </div>

      <div>
        <MintedNFTs refresh={minted} /> {/* Pass the minted state to MintedNFTs */}
      </div>
    </div>
  );
}

export default Home;
