import { useEffect, useState } from "react";
import { callReadOnlyFunction } from "@stacks/transactions";
import { StacksMocknet } from "@stacks/network";
import { uintCV } from "@stacks/transactions";
import { userSession } from "./ConnectWallet";

const MintedNFTs = ({ minted }) => {
  const [nfts, setNfts] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  
  const contractAddress = "ST1PQHQKV0RJXZFY1DGX8MNSNYVE3VGZJSRTPGZGM";
  const contractName = "sip009-nft";

  const fetchNFTs = async () => {
    setLoading(true);
    setError(null);
    
    const network = new StacksMocknet();
    const senderAddress = userSession.loadUserData().profile.stxAddress.testnet;

    try {
      // Fetch the last token ID
      const options = {
        contractAddress,
        contractName,
        functionName: "get-last-token-id",
        network,
        functionArgs: [],
        senderAddress,
      };

      const lastTokenIdResult = await callReadOnlyFunction(options);
      if (lastTokenIdResult && lastTokenIdResult.value) {
        const lastTokenId = parseInt(lastTokenIdResult.value.value, 10); // Correctly convert `u` prefixed value

        const mintedNfts = [];
        // Iterate through all token IDs starting from 1
        for (let tokenId = 1; tokenId <= lastTokenId; tokenId++) {
          const ownerOptions = {
            contractAddress,
            contractName,
            functionName: "get-owner",
            functionArgs: [uintCV(tokenId)], // Ensure this is the correct ID for your contract
            network,
            senderAddress,
          };

          const ownerResult = await callReadOnlyFunction(ownerOptions);
          const owner = ownerResult?.value?.value?.toString() || "Unknown";
          
          mintedNfts.push({ tokenId: `u${tokenId}`, owner }); // Ensure the ID is prefixed with `u`
        }

        setNfts(mintedNfts); // Update state with minted NFTs
      } else {
        setError("Failed to fetch last token ID");
      }
    } catch (error) {
      console.error("Error fetching NFTs:", error);
      setError("Error fetching NFTs");
    } finally {
      setLoading(false);
    }
  };

  // Trigger fetchNFTs whenever the minted state changes
  useEffect(() => {
    fetchNFTs();
  }, [minted]);

  if (loading) {
    return <p>Loading NFTs...</p>;
  }

  if (error) {
    return <p>Error: {error}</p>;
  }

  return (
    <div>
      <h2>Minted NFTs</h2>
      {nfts.length === 0 ? (
        <p>No NFTs have been minted yet.</p>
      ) : (
        <ul>
          {nfts.map((nft) => (
            <li key={nft.tokenId}>
              Token ID: {nft.tokenId}, Owner: {nft.owner}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default MintedNFTs;
