// LIBRARY IMPORTS
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, Navigate } from 'react-router-dom';

// CSS FILES
import "./App.css";

// PAGE FILES
import Home from './components/pages/home.js';
import Profile from './components/pages/profile.js';
import AssetSell from './components/pages/AssetSell.js';
import AssetPurchase from './components/pages/AssetPurchase.js';
import TransactionHistory from './components/pages/transactionHistory.js';

// CONTRACT FUNCTIONS
import ConnectWallet from "./components/ConnectWallet";
import Mint from "./components/Mint";
import { userSession } from "./components/ConnectWallet"; // Assuming userSession is managed here

// ProtectedRoute Component
function ProtectedRoute({ children }) {
  const isUserSignedIn = userSession.isUserSignedIn(); // Check login status

  if (!isUserSignedIn) {
    // If not signed in, alert the user and redirect to home
    window.alert("You must sign in to access this page.");
    return <Navigate to="/" replace />;
  }

  return children; // If signed in, render the protected component
}

function App() {
  return (
    <Router>
      <div className="App">
        {/* Navbar will always be visible */}
        <nav>
          <div className="nav-left">
            <ConnectWallet />
          </div>
          <div className="nav-center">
            <Link to="/" className="App-link">Tiny Market</Link>
          </div>
          <div className="nav-menu">
            <Link to="/" className="App-link">Home</Link>
            <Link to="/profile" className="App-link">Profile</Link>
            <Link to="/assetsell" className="App-link">Sell or Swap</Link>
            <Link to="/assetpurchase" className="App-link">Purchase</Link>
            <Link to="/transactions" className="App-link">Transactions</Link>
          </div>
        </nav>

        {/* Routes */}
        <Routes>
          <Route path="/" element={<Home />} />
          
          {/* Use ProtectedRoute to wrap routes that require login */}
          <Route
            path="/profile"
            element={
              <ProtectedRoute>
                <Profile />
              </ProtectedRoute>
            }
          />
          <Route
            path="/assetsell"
            element={
              <ProtectedRoute>
                <AssetSell />
              </ProtectedRoute>
            }
          />
          <Route
            path="/assetpurchase"
            element={
              <ProtectedRoute>
                <AssetPurchase />
              </ProtectedRoute>
            }
          />
          <Route
            path="/transactions"
            element={
              <ProtectedRoute>
                <TransactionHistory />
              </ProtectedRoute>
            }
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
