import { useCallback, useEffect, useState } from "react";
import { useConnect } from "@stacks/connect-react";
import { StacksMocknet } from "@stacks/network";
import {
  AnchorMode,
  uintCV,
  callReadOnlyFunction,
  principalCV,
  makeStandardSTXPostCondition,
  FungibleConditionCode
} from "@stacks/transactions";
import { userSession } from "./ConnectWallet";

const Whitelist = () => {
  const { doContractCall } = useConnect();
  const [src, setSrc] = useState('');
  const [whitelisted, setWhitelisted] = useState(false);
  const userAddress = userSession.loadUserData().profile.stxAddress.testnet;

  function isWhitelisted() {
    const postConditionAddress = userSession.loadUserData().profile.stxAddress.testnet;
    const postConditionCode = FungibleConditionCode.LessEqual;
    const postConditionAmount = 50 * 1000000;

    const contractName = "sip009-nft";
    const isWhiltelisted = true;

    doContractCall({
      network: new StacksMocknet(),
      anchorMode: AnchorMode.Any,
      contractAddress: "ST1PQHQKV0RJXZFY1DGX8MNSNYVE3VGZJSRTPGZGM",
      contractName: "tiny-market",
      functionName: "mint",
      functionArgs: [
        contractName,
        isWhiltelisted
      ],
      postConditions: [
        makeStandardSTXPostCondition(
          postConditionAddress,
          postConditionCode,
          postConditionAmount
        )
      ],
      onFinish: (data) => {
        window.alert("sip009-nft Whitelisted Successfully");
      },
      onCancel: () => {
        console.log("onCancel:", "Transaction was canceled");
        window.alert("NFT whitelist failed");
      },
    });
    
  }


  if (!userSession.isUserSignedIn()) {
    return null;
  }
  
  return (
    <div>
      {!minted &&
        <div>
          <p>Mint Another Ape!</p>
          <button className="MintButton" onClick={() => isWhitelisted()}>
            Mint Ape NFT üêµ
          </button>
        </div>  
      }
      {minted && 
        <div>
          <img src={src} alt="another ape" height="500px" width="500px" />
        </div>
      }
    </div>
  ); 
};

export default Whitelist;
