import { useCallback, useEffect, useState } from "react";
import { useConnect } from "@stacks/connect-react";
import { StacksMocknet } from "@stacks/network";
import {
  AnchorMode,
  uintCV,
  callReadOnlyFunction,
  makeStandardSTXPostCondition,
  FungibleConditionCode
} from "@stacks/transactions";
import { userSession } from "./ConnectWallet";
import fleekStorage from '@fleekhq/fleek-storage-js';

const Mint = () => {
  const { doContractCall } = useConnect();
  const [minted, setMinted] = useState(false);
  const [src, setSrc] = useState('');

  function mint() {
    const postConditionAddress = userSession.loadUserData().profile.stxAddress.testnet;
    const postConditionCode = FungibleConditionCode.LessEqual;
    const postConditionAmount = 50 * 1000000;

    doContractCall({
      network: new StacksMocknet(),
      anchorMode: AnchorMode.Any,
      contractAddress: "ST1PQHQKV0RJXZFY1DGX8MNSNYVE3VGZJSRTPGZGM",
      contractName: "sip009-nft",
      functionName: "mint",
      functionArgs: [],
      postConditions: [
        makeStandardSTXPostCondition(
          postConditionAddress,
          postConditionCode,
          postConditionAmount
        )
      ],
      onFinish: (data) => {
        window.alert("NFT Successful");
        console.log("onFinish:", data);
        console.log("Explorer:", `localhost:8000/txid/${data.txId}?chain=testnet`)
        setMinted(true);
      },
      onCancel: () => {
        console.log("onCancel:", "Transaction was canceled");
        window.alert("NFT mint failed");
      },
    });
  }

  const getNft = useCallback(async () => {
    if (minted) {
      const options = {
          contractAddress: "ST1PQHQKV0RJXZFY1DGX8MNSNYVE3VGZJSRTPGZGM",
          contractName: "sip009-nft",
          functionName: "get-token-uri",
          network: new StacksMocknet(),
          functionArgs: [uintCV(1)],
          senderAddress: userSession.loadUserData().profile.stxAddress.testnet
      };

      const result = await callReadOnlyFunction(options);
      console.log(result);
      if (result.value) {
        // using fetch to retrieve data
        // fetch(`https://${result.value.value.data}`)
        // .then(res => res.json())
        // .then ((output) => {
        //   console.log("Metadata:", output)
        //   setSrc(output.image);
        // })

        // using fleek helper to retrieve data
        const myFile = await fleekStorage.getFileFromHash({
          // hash can be passed in via get-token-uri as in fetch example
          hash: 'bafkreignszpqznkmvr6aqgxilmrrq4wos7igeeex7iajfic3ic5dkocndi'
        })
        console.log("MY FILE", myFile)
        setSrc(myFile.image);
      }
    }
  });
  console.log(src);

  useEffect(() => {
    getNft();
  }, [minted])

  if (!userSession.isUserSignedIn()) {
    return null;
  }
  
  return (
    <div>
      {!minted &&
        <div>
          <p>Mint Another Ape!</p>
          <button className="MintButton" onClick={() => mint()}>
            Mint Ape NFT üêµ
          </button>
        </div>  
      }
      {minted && 
        <div>
          <img src={src} alt="another ape" height="300px" width="300px" />
        </div>
      }
    </div>
  ); 
};

export default Mint;
